def set_as_environment_variables(self) -> bool:
    """
    Load the current dotenv as system environment variable.
    """
    if not self.dict():
        return False

    for k, v in self.dict().items():
        if k in os.environ and not self.override:
            continue
        if v is not None:
            os.environ[k] = v

    return True

